package week1.day2;

public class NumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int version = 10;
		if (version == 0) {
			System.out.println("The version is neutral");
		}else if (version >0) {
			System.out.println("The version is positive ");
		}else {
			System.out.println("The version is Negative");
		}

	}

}
